import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchApiData } from '../redux/apiSlice';
import { Col, Row } from 'antd';
import sample1 from "../../assets/images/sample.png"
import styles from "./style.module.scss"

const Cards = () => {
    const dispatch = useDispatch();
    const apiData = useSelector((state) => state.api.data);
    const loading = useSelector((state) => state.api.loading);
    const error = useSelector((state) => state.api.error);

    const fetchDataFromAPIWithRetry = async (retryCount = 0) => {
        if (retryCount < 3) {
            try {
                await dispatch(fetchApiData());
            } catch (error) {
                console.error('Error fetching data:', error);
                setTimeout(() => fetchDataFromAPIWithRetry(retryCount + 1), 1000 * Math.pow(2, retryCount));
            }
        }
    };

    useEffect(() => {
        if (loading === 'idle') {
            fetchDataFromAPIWithRetry();
        }
    }, [loading]);

    return (
        <div className={styles.mainWrapper}>
            <h1 className={styles.Labelheading}>Unsplashed Gallery Design</h1>
            {loading === 'loading' ? (
                <div>Loading...</div>
            ) : loading === 'failed' ? (
                <div>Error: {error}</div>
            ) : (
                <div>
                    <Row gutter={[60, 60]}>
                        {apiData.photos?.map((item) => (
                            <Col xs={12} md={8} key={item.id}>
                                <img src={item.url ? item.url : sample1} alt="Cat" height="100%" width="100%" style={{ boxShadow: "0px 2px 10px 2px black", borderRadius: 5 }} />
                            </Col>
                        ))}
                    </Row>
                </div>
            )}
        </div>
    );
};

export default Cards;
