import { Col, Row } from 'antd';
import React, { useEffect, useState } from 'react';
import sampleImg from "../../assets/images/sample.png";

const Cards = () => {
    const [catsData, setCatsData] = useState([]);



    useEffect(() => {
        async function fetchData() {
            try {
                const response = await fetch("https://api.thecatapi.com/v1/images/search?limit=80&mime_types=&order=Random&size=small&page=3&sub_id=demo-ce06ee");
                if (response.ok) {
                    const data = await response.json();
                    setCatsData(data);
                } else {
                    throw new Error('Network response was not ok');
                }
            } catch (error) {
                console.error('Error fetching data:', error);

                // Retry after a short delay (e.g., 1 second)
                setTimeout(fetchData, 1000);
            }
        }

        fetchData();
    }, []);

    return (
        <div>
            <h3>Cards</h3>
            <Row gutter={12}>
                {catsData.length ?
                    catsData.slice(0, 10).map((items, ind) => {
                        console.log('items -->>', items);
                        return (
                            <Col xs={24} md={8} key={items.id}>
                                <div>
                                <img src={items.url} alt={`cat : ${items.id}`} height={300} width={"100%"} />
                                </div>
                            </Col>
                        );
                    }) :
                    <div>Loading .....</div>
                }
            </Row>
        </div>
    );
};

export default Cards;
