import { Col, Row } from 'antd'
import React, { useEffect, useState } from 'react'
import sampleImg from "../../assets/images/sample.png"

const Cards = () => {
    const [catsData, setCatsData] = useState('')

    useEffect(async () => {
        const data = await fetch("https://api.thecatapi.com/v1/images/search?limit=80&mime_types=&order=Rand om&size=small&page=3&sub_id=demo-ce06ee")
            .then(response => response.json())
            .then(resp => setCatsData(resp))
        // fetchData()
    }, [])

    // const fetchData = async () => {
    //     const data = await fetch("https://api.thecatapi.com/v1/images/search?limit=80&mime_types=&order=Rand om&size=small&page=3&sub_id=demo-ce06ee")
    //         .then(response => response.json())
    //         .then(resp => setCatsData(resp))
    // }
    return (
        <div>
            <h3>Cards</h3>
            <Row>
                {
                    catsData.length ?
                        catsData.slice(0, 10).map((items, ind) => {
                            console.log('items -->>', items)
                            return (
                                <Col xs={8} key={items.id}>
                                    <img src={items.url} />
                                </Col>
                            )
                        })
                        :
                        <div>Loading .....</div>
                }
            </Row>
        </div>
    )
}

export default Cards
