import React, { useEffect, useState } from 'react'
import Header from '../header/Header'
import { useDispatch, useSelector } from 'react-redux'
import { Badge, Button, Card, Col, Row } from 'antd'
import styles from "./style.module.scss"
import { ArrowLeftOutlined } from '@ant-design/icons';
import { Link } from 'react-router-dom'
import { updateLocalData } from '../redux/apiSlice'

const Gallery = () => {
  const dispatch = useDispatch();
  const [localList, setLocalList] = useState("")
  const stateLocalData = useSelector((state) => state.api.tag)

  useEffect(() => {
    stateLocalData && setLocalList(stateLocalData)
  }, [])

  const removeCardFromList = (tag) => {
    console.log('first time it was', stateLocalData)
    const UpdatedArray = stateLocalData.filter((item, ind) => {
      return (item.tag !== tag)
    })
    const updatedArr =  UpdatedArray.map((item, ind) => {
      dispatch(updateLocalData({ id: item.id, url: item.url, tag: item.tag }))
    })
    // console.log('UpdatedArray',UpdatedArray)
    // dispatch(updateLocalData(UpdatedArray))
    setLocalList(updatedArr);
  }
  return (
    <>
      <Header />
      <div className={styles.galleryWrapper}>
        <div className={styles.goBackBtn}>
          <Link to="/"><Button icon={<ArrowLeftOutlined />}>Go back</Button></Link>
        </div>
        <Row gutter={[10, 10]}>
          {
            stateLocalData.length ?
              stateLocalData?.map((items, ind) => {
                return (
                  <Col xs={24} sm={12} md={8} >
                    <Card>
                      <img src={items.url} height="100%" width="100%" />
                      <div className={styles.actionBtnWrapper}>
                        <Badge count={items.tag} />
                        <Button type='dashed' onClick={() => { removeCardFromList(items.tag) }}>Remove</Button>
                      </div>
                    </Card>
                  </Col>
                )
              })
              :
              (<h1>Nothing in here..</h1>)
          }
        </Row>
      </div>

    </>
  )
}

export default Gallery
