import {
    RadiusBottomleftOutlined,
    RadiusBottomrightOutlined,
    RadiusUpleftOutlined,
    RadiusUprightOutlined,
} from '@ant-design/icons';
import React, { useMemo } from 'react';
import { Button, Divider, Space, notification } from 'antd';
const Context = React.createContext({
    name: 'Default',
});
const NotificationHighlights = ({addTag,handleAddTag}) => {
    const [api, contextHolder] = notification.useNotification();

    const openNotification = (tag) => {
        api.info({
            message: `Card Notification`,
            description: <Context.Consumer>{({ name }) => `Added Card #${tag} Successfully`}</Context.Consumer>,
            placement: 'topRight',
        });
    };
    const contextValue = useMemo(
        () => ({
            name: 'Card Notification',
        }),
        [],
    );
    return (
        <Context.Provider value={contextValue}>
            {contextHolder}
            <Space style={{ position: 'fixed' }}>
                {/* <Button
                    type="primary"
                    onClick={() => openNotification('topRight')}
                    icon={<RadiusUprightOutlined />}
                >
                    topRight
                </Button> */}
                <button className={styles.addTag} onClick={handleAddTag}>Add</button>
            </Space>
        </Context.Provider>
    );
};
export default NotificationHighlights;
