import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchApiData, deleteApiData } from '../redux/apiSlice';
import { Col, Row } from 'antd';
import sample1 from "../../assets/images/sample.png"
import styles from "./style.module.scss"
import { MoneyCollectOutlined, DeleteOutlined, SettingOutlined, CloudDownloadOutlined } from "@ant-design/icons"

const Cards = () => {
    const dispatch = useDispatch();
    const apiData = useSelector((state) => state.api.data);
    const loading = useSelector((state) => state.api.loading);
    const error = useSelector((state) => state.api.error);

    const fetchDataFromAPIWithRetry = async (retryCount = 0) => {
        if (retryCount < 3) {
            try {
                await dispatch(fetchApiData());
            } catch (error) {
                console.error('Error fetching data:', error);
                setTimeout(() => fetchDataFromAPIWithRetry(retryCount + 1), 1000 * Math.pow(2, retryCount));
            }
        }
    };

    useEffect(() => {
        if (loading === 'idle') {
            fetchDataFromAPIWithRetry();
        }
    }, [loading]);

    const handleDelete = (id) => {
        dispatch(deleteApiData(id))
    }
    useEffect(()=>{
        !apiData.length && fetchApiData()
    },[apiData])

    return (
        <div className={styles.mainWrapper}>
            <h1 className={styles.Labelheading}>Unsplashed Gallery Design</h1>
            {loading === 'loading' ? (
                <div>Loading...</div>
            ) : loading === 'failed' ? (
                <div>Error: {error}</div>
            ) : (
                <div>
                    <Row gutter={[60, 60]} className={styles.mainRow}>
                        {
                            !apiData.length ? <div>loading...</div> : apiData?.map((item) => (
                                <Col xs={24} sm={12} md={8} key={item.id}>
                                    <div className={styles.cardsWrapper}>
                                        <img src={item.url ? item.url : sample1} alt="img" height="100%" width="100%" style={{ boxShadow: "0px 2px 10px 2px black", borderRadius: 5 }} />
                                        <div className={styles.iconsWrapper}>
                                            <MoneyCollectOutlined className={styles.image} style={{ fontSize: 30 }} />
                                            <CloudDownloadOutlined className={styles.image} style={{ fontSize: 30 }} />
                                            <DeleteOutlined onClick={() => { handleDelete(item.id) }} className={styles.image} style={{ fontSize: 30 }} />
                                            <SettingOutlined className={styles.image} style={{ fontSize: 30 }} />
                                        </div>
                                    </div>
                                </Col>
                            ))}
                    </Row>
                </div>
            )}
        </div>
    );
};

export default Cards;
