import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchApiData } from '../redux/apiSlice';
import { Col, Row } from 'antd';
import sample1 from "../../assets/images/sample.png"

const Cards = () => {
    const dispatch = useDispatch();
    const apiData = useSelector((state) => state.api.data);
    const loading = useSelector((state) => state.api.loading);
    const error = useSelector((state) => state.api.error);

    const fetchDataFromAPIWithRetry = async (retryCount = 0) => {
        if (retryCount < 10) {
            try {
                await dispatch(fetchApiData());
            } catch (error) {
                console.error('Error fetching data:', error);
                setTimeout(() => fetchDataFromAPIWithRetry(retryCount + 1), 1000 * Math.pow(2, retryCount));
            }
        }
    };

    useEffect(() => {
        if (loading === 'idle') {
            fetchDataFromAPIWithRetry();
        }
    }, [loading]);

    return (
        <div>
            <h3>Cards</h3>
            {loading === 'loading' ? (
                <div>Loading...</div>
            ) : loading === 'failed' ? (
                <div>Error: {error}</div>
            ) : (
                <Row gutter={[20,20]}>
                    {apiData.photos.map((item) => (
                        <Col xs={8} key={item.id}>
                            <img src={item.url ? item.url : sample1} alt="Cat" height="100%" width="100%" />
                        </Col>
                    ))}
                </Row>
            )}
        </div>
    );
};

export default Cards;
