import React from 'react';
import { DownOutlined } from '@ant-design/icons';
import { Button, Dropdown, message, Space } from 'antd';
import { UserOutlined } from "@ant-design/icons"
const onClick = ({ key }) => {
  message.info(`Click on item ${key}`);
};

const handleMenuClick = (e) => {
  console.log('clicked heree ___>>>')
  message.info('Click on menu item.',e,e);
  console.log('click', e);
};

const items = [
  {
    label: '1st menu item',
    key: '1',
    icon: <UserOutlined />,
  },
  {
    label: '2nd menu item',
    key: '2',
    icon: <UserOutlined />,
  },
  {
    label: '3rd menu item',
    key: '3',
    icon: <UserOutlined />,
    danger: true,
  },
  {
    label: '4rd menu item',
    key: '4',
    icon: <UserOutlined />,
    danger: true,
    disabled: false,
  },
];

const menuProps = {
  items,
  onClick: ()=>{handleMenuClick()},
};



const CustomDropDown = () => (
  <Dropdown
    menu={{
      items,
      onClick,
    }}
  >
    <a onClick={(e) => e.preventDefault()}>
      <Space>
        Hover me, Click menu item
        <DownOutlined />
      </Space>
    </a>
  </Dropdown>
);
export default CustomDropDown;
