import React, { useEffect, useState } from 'react'
import Header from '../header/Header'
import { useSelector } from 'react-redux'
import { Badge, Button, Card, Col, Row } from 'antd'
import styles from "./style.module.scss"
import { ArrowLeftOutlined } from '@ant-design/icons';
import { Link } from 'react-router-dom'

const Gallery = () => {
  const [localList, setLocalList] = useState("")
  const updateLocalData = useSelector((state) => state.api.tag)

  useEffect(() => {
    updateLocalData && setLocalList(updateLocalData)
  }, [])
  const removeCardFromList = (id) => {
    console.log('first time it was', updateLocalData)
    const UpdatedArray = updateLocalData.filter((item, ind) => {
      return (item.id !== id)
    })
    setLocalList(UpdatedArray);
    console.log('this is items id -->>', UpdatedArray)
  }
  return (
    <>
      <Header />
      <div className={styles.galleryWrapper}>
        <div className={styles.goBackBtn}>
          <Link to="/"><Button icon={<ArrowLeftOutlined />}>Go back</Button></Link>
        </div>
        <Row gutter={[10, 10]}>
          {
            localList.length ?
              localList?.map((items, ind) => {
                return (
                  <Col xs={24} sm={12} md={8} >
                    <Card>
                      <img src={items.url} height="100%" width="100%" />
                      <div className={styles.actionBtnWrapper}>
                        <Badge count={items.tag} />
                        <Button type='dashed' onClick={() => { removeCardFromList(items.id) }}>Remove</Button>
                      </div>
                    </Card>
                  </Col>
                )
              })
              :
              (<h1>Nothing in here..</h1>)
          }
        </Row>
      </div>

    </>
  )
}

export default Gallery
