import { configureStore } from '@reduxjs/toolkit';
import apiReducer from './apiSlice'; // Import the API slice you created

const store = configureStore({
  reducer: {
    api: apiReducer, // Add your API slice to the store
    // You can include other slices as needed
  },
});

export default store;
