import { useDispatch, useSelector } from 'react-redux';
import { fetchApiData } from './apiSlice';

const MyComponent = () => {
  const dispatch = useDispatch();
  const apiData = useSelector((state) => state.api.data);
  const loading = useSelector((state) => state.api.loading);
  const error = useSelector((state) => state.api.error);

  const fetchDataFromAPIWithRetry = async (retryCount = 0) => {
    if (retryCount < 3) { // Retry up to 3 times
      try {
        await dispatch(fetchApiData());
      } catch (error) {
        console.error('Error fetching data:', error);
        // Retry with exponential backoff
        setTimeout(() => fetchDataFromAPIWithRetry(retryCount + 1), 1000 * Math.pow(2, retryCount));
      }
    }
  };

  useEffect(() => {
    if (loading === 'idle') {
      fetchDataFromAPIWithRetry();
    }
  }, [loading]);

  return (
    <div>
      <h3>Cards</h3>
      {loading === 'loading' ? (
        <div>Loading...</div>
      ) : loading === 'failed' ? (
        <div>Error: {error}</div>
      ) : (
        <Row>
          {apiData.slice(0, 10).map((item) => (
            <Col xs={8} key={item.id}>
              <img src={item.url} alt="Cat" />
            </Col>
          ))}
        </Row>
      )}
    </div>
  );
};
