import React, { useState } from 'react';
import { DownOutlined } from '@ant-design/icons';
import { Dropdown, message, Space, Menu } from 'antd';

const CustomDropDown = () => {
  const [currState, setCurrState] = useState([])
  const items = [
    {
      label: 'Nature',
      key: '1',
    },
    {
      label: 'City',
      key: '2',
    },
    {
      label: 'Work',
      key: '3',
    },
    {
      label: 'Internet',
      key: '4',
    },
  ];

  const onClick = (item) => {
    const currTag = items.find(i => i.key === item.key).label
    setCurrState(prevState => [...prevState, currTag])
    console.log('currState is ->', currState)
    // message.info(`Click on item ${items.find(i => i.key === item.key).label}`);
  };

  const menu = (
    <Menu onClick={onClick}>
      {items.map((item) => (
        <Menu.Item key={item.key}>{item.label}</Menu.Item>
      ))}
    </Menu>
  );

  return (
    <Dropdown overlay={menu}>
      <a onClick={(e) => e.preventDefault()}>
        <Space>
          Add Tag
          <DownOutlined />
        </Space>
      </a>
    </Dropdown>
  );
};

export default CustomDropDown;
